(()=>{const e=document.getElementById("buttonInstall");window.addEventListener("beforeinstallprompt",(t=>{t.preventDefault(),window.deferredPrompt=t,e.style.display="block"})),e.addEventListener("click",(async()=>{e.style.display="none";const t=window.deferredPrompt;if(!t)return;t.prompt();const n=await t.userChoice;window.deferredPrompt=null,"accepted"===n.outcome?console.log("User accepted the install prompt"):console.log("User dismissed the install prompt")})),window.addEventListener("appinstalled",(e=>{console.log("App was successfully installed!")}))})();